import React from 'react';

const ShopProductGrid = ({ products, productsPerPage, currentPage, onPageChange, onViewDetails }) => {
  const startIndex = (currentPage - 1) * productsPerPage;
  const endIndex = startIndex + productsPerPage;
  const currentProducts = products.slice(startIndex, endIndex);
  const totalPages = Math.ceil(products.length / productsPerPage);

  return (
    <section className="py-12 px-4 sm:px-6 lg:px-8 bg-gradient-to-br from-white to-gray-50">
      <h2 className="text-4xl sm:text-5xl font-extrabold text-center text-gray-900 mb-10 sm:mb-12">
        Nuestros Productos Estrella
      </h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 sm:gap-8 lg:gap-10 max-w-6xl mx-auto">
        {currentProducts.length > 0 ? (
          currentProducts.map((product) => (
            <div
              key={product.id}
              className="bg-white rounded-2xl shadow-xl overflow-hidden transform transition-all duration-300 ease-in-out hover:scale-105 hover:shadow-2xl group"
            >
              <div className="relative overflow-hidden">
                <img
                  src={product.image}
                  alt={product.name}
                  className="w-full h-48 sm:h-56 md:h-64 object-cover transition-transform duration-300 group-hover:scale-110"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-end p-4 sm:p-6">
                  <button
                    onClick={() => onViewDetails(product)}
                    className="w-full py-2 sm:py-3 bg-orange-600 text-white font-semibold rounded-lg opacity-0 group-hover:opacity-100 transform translate-y-4 group-hover:translate-y-0 transition-all duration-300 ease-in-out text-base sm:text-lg"
                  >
                    Ver Detalles
                  </button>
                </div>
              </div>
              <div className="p-4 sm:p-6">
                <h3 className="text-xl sm:text-2xl font-semibold text-gray-800 mb-1 sm:mb-2">{product.name}</h3>
                <p className="text-lg sm:text-xl text-orange-600 font-bold">${product.price} MXN</p>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-600 text-lg sm:text-xl col-span-full">No se encontraron productos que coincidan con tu b√∫squeda.</p>
        )}
      </div>

      {totalPages > 1 && (
        <div className="flex justify-center mt-12 space-x-4">
          <button
            onClick={() => onPageChange(currentPage - 1)}
            disabled={currentPage === 1}
            className="px-6 py-3 bg-gray-800 text-white rounded-full hover:bg-black disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-300"
          >
            Anterior
          </button>
          {Array.from({ length: totalPages }, (_, i) => (
            <button
              key={i + 1}
              onClick={() => onPageChange(i + 1)}
              className={`px-6 py-3 rounded-full font-semibold transition-colors duration-300 ${
                currentPage === i + 1 ? 'bg-orange-600 text-white' : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
              }`}
            >
              {i + 1}
            </button>
          ))}
          <button
            onClick={() => onPageChange(currentPage + 1)}
            disabled={currentPage === totalPages}
            className="px-6 py-3 bg-gray-800 text-white rounded-full hover:bg-black disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-300"
          >
            Siguiente
          </button>
        </div>
      )}
    </section>
  );
};

export default ShopProductGrid;
