import React, { useState, useEffect } from "react";
import { db } from "../firebase"; // Asegúrate que el path es correcto
import { collection, addDoc, getDocs, deleteDoc, doc } from "firebase/firestore";
// Si usas subida de imágenes, también necesitarás el módulo de storage.

const AdminNovedades = () => {
  const [novedades, setNovedades] = useState([]);
  const [form, setForm] = useState({
    nombre: "",
    imagenUrl: "",
    precio: "",
    descripcion: "",
    instagramUrl: "",
  });

  // Cargar novedades existentes
  useEffect(() => {
    const fetchNovedades = async () => {
      const querySnapshot = await getDocs(collection(db, "novedades"));
      setNovedades(querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    };
    fetchNovedades();
  }, []);

  // Manejar cambios de campos
  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Guardar novedad (solo con URL de imagen por ahora)
  const handleSubmit = async e => {
    e.preventDefault();
    await addDoc(collection(db, "novedades"), form);
    setForm({
      nombre: "",
      imagenUrl: "",
      precio: "",
      descripcion: "",
      instagramUrl: "",
    });
    // Actualizar lista
    const querySnapshot = await getDocs(collection(db, "novedades"));
    setNovedades(querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
  };

  // Eliminar novedad
  const handleDelete = async (id) => {
    await deleteDoc(doc(db, "novedades", id));
    setNovedades(novedades.filter(n => n.id !== id));
  };

  return (
    <div>
      <h2>Gestionar Novedades</h2>
      <form onSubmit={handleSubmit}>
        <input name="nombre" value={form.nombre} onChange={handleChange} placeholder="Nombre del producto" required />
        <input name="precio" value={form.precio} onChange={handleChange} placeholder="Precio" required />
        <input name="imagenUrl" value={form.imagenUrl} onChange={handleChange} placeholder="URL de la imagen" required />
        <input name="instagramUrl" value={form.instagramUrl} onChange={handleChange} placeholder="URL Instagram (opcional)" />
        <textarea name="descripcion" value={form.descripcion} onChange={handleChange} placeholder="Historia/Descripción" required />
        <button type="submit">Agregar Novedad</button>
      </form>

      <ul>
        {novedades.map(n => (
          <li key={n.id}>
            <img src={n.imagenUrl} alt={n.nombre} width={80} height={80} />
            <strong>{n.nombre}</strong> - {n.precio}
            <button onClick={() => handleDelete(n.id)}>Eliminar</button>
            {/* Puedes agregar botón editar aquí si quieres */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminNovedades;
