// src/components/GallerySection.js
import React from 'react';

const images = [
  "https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80",
  "https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=crop&w=400&q=80",
  "https://images.unsplash.com/photo-1465101162946-4377e57745c3?auto=format&fit=crop&w=400&q=80",
  "https://images.unsplash.com/photo-1454023492550-5696f8ff10e1?auto=format&fit=crop&w=400&q=80",
  "https://images.unsplash.com/photo-1444065381814-865dc9da92c0?auto=format&fit=crop&w=400&q=80",
  "https://images.unsplash.com/photo-1506084868230-bb9d95c24759?auto=format&fit=crop&w=400&q=80"
];

const instagramUrl = "https://www.instagram.com/tu_usuario/"; // Cambia por tu IG

const GallerySection = () => (
  <section className="py-16 px-4 sm:px-6 lg:px-8 bg-gradient-to-br from-orange-50 to-yellow-50 text-center">
    <h2 className="text-4xl sm:text-5xl font-extrabold text-gray-900 mb-8">Galería Real</h2>
    <div className="grid grid-cols-2 sm:grid-cols-3 gap-4 max-w-4xl mx-auto">
      {images.map((img, idx) => (
        <a key={idx} href={instagramUrl} target="_blank" rel="noopener noreferrer">
          <img
            src={img}
            alt={`galería ${idx + 1}`}
            className="rounded-2xl shadow-lg hover:scale-105 hover:shadow-2xl transition-transform duration-300 cursor-pointer"
          />
        </a>
      ))}
    </div>
    <a
      href={instagramUrl}
      target="_blank"
      rel="noopener noreferrer"
      className="inline-block mt-8 text-orange-600 font-bold text-xl hover:underline"
    >
      Ver más en Instagram →
    </a>
  </section>
);

export default GallerySection;
