import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

// Flechas modernas y grandes
function NextArrow(props) {
  const { onClick } = props;
  return (
    <button
      className="absolute right-0 md:right-3 top-1/2 -translate-y-1/2 z-10 bg-white hover:bg-orange-600 text-orange-500 hover:text-white rounded-full shadow-2xl p-4 text-4xl transition-all border-2 border-orange-200"
      onClick={onClick}
      aria-label="Siguiente"
      style={{ outline: 'none' }}
    >›</button>
  );
}

function PrevArrow(props) {
  const { onClick } = props;
  return (
    <button
      className="absolute left-0 md:left-3 top-1/2 -translate-y-1/2 z-10 bg-white hover:bg-orange-600 text-orange-500 hover:text-white rounded-full shadow-2xl p-4 text-4xl transition-all border-2 border-orange-200"
      onClick={onClick}
      aria-label="Anterior"
      style={{ outline: 'none' }}
    >‹</button>
  );
}

// Personaliza los dots en CSS global (App.css o index.css):
/*
.slick-dots li button:before {
  font-size: 18px !important;
  color: #fb923c !important;
  opacity: 0.5 !important;
}
.slick-dots li.slick-active button:before {
  color: #ea580c !important;
  opacity: 1 !important;
}
*/

const ShopProductGrid = ({ products, onViewDetails }) => {
  const settings = {
    dots: true,
    infinite: products.length > 5,
    speed: 550,
    slidesToShow: 5,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    pauseOnHover: true,
    responsive: [
      { breakpoint: 1536, settings: { slidesToShow: 5 } },
      { breakpoint: 1280, settings: { slidesToShow: 4 } },
      { breakpoint: 1024, settings: { slidesToShow: 3 } },
      { breakpoint: 800,  settings: { slidesToShow: 2 } },
      { breakpoint: 640,  settings: { slidesToShow: 1 } }
    ]
  };

  return (
    <section className="py-12 px-2 sm:px-6 lg:px-8 bg-gradient-to-br from-white to-gray-50 relative">
      <h2 className="text-4xl sm:text-5xl font-extrabold text-center text-gray-900 mb-10 sm:mb-12 drop-shadow-md tracking-tight">
        Nuestros Productos Estrella
      </h2>
      <div className="relative max-w-7xl mx-auto pb-6">
        <Slider {...settings}>
          {products.map((product) => (
            <div key={product.id} className="px-3 py-1">
              <div className="bg-white rounded-2xl shadow-xl overflow-hidden transform transition-all duration-300 ease-in-out hover:scale-105 hover:shadow-orange-300 hover:shadow-2xl group relative">
                <div className="relative overflow-hidden">
                  <img
                    src={product.image}
                    alt={product.name}
                    className="w-full h-48 sm:h-56 md:h-64 object-cover transition-transform duration-300 group-hover:scale-110"
                  />
                  <span className="absolute top-2 left-2 bg-orange-600 text-white text-xs font-bold rounded-full px-3 py-1 shadow-sm opacity-90 uppercase tracking-widest pointer-events-none select-none"
                    style={{display: (product.nuevo || product.masVendido) ? 'inline-block':'none'}}
                  >
                    {product.nuevo ? "NUEVO" : product.masVendido ? "DESTACADO" : ""}
                  </span>
                  <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-end p-4 sm:p-6">
                    <button
                      onClick={() => onViewDetails && onViewDetails(product)}
                      className="w-full py-2 sm:py-3 bg-orange-600 text-white font-semibold rounded-lg opacity-0 group-hover:opacity-100 transform translate-y-4 group-hover:translate-y-0 transition-all duration-300 ease-in-out text-base sm:text-lg shadow-lg"
                    >
                      Ver Detalles
                    </button>
                  </div>
                </div>
                <div className="p-3 sm:p-4">
                  <h3 className="text-xl sm:text-2xl font-semibold text-gray-800 mb-1 sm:mb-2 text-center truncate">{product.name}</h3>
                  <p className="text-lg sm:text-xl text-orange-600 font-bold text-center">${product.price} MXN</p>
                </div>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </section>
  );
};

export default ShopProductGrid;
