import React from 'react';

// Cambia las URLs por las de tus productos reales cuando las tengas
const images = [
  "/mnt/data/2683d277-bbcc-4490-af27-15191fc47de2.png", // Tu imagen subida
  "https://images.unsplash.com/photo-1519864600265-abb23847ef2c?auto=format&fit=crop&w=400&q=80", // Frappé/Granizado
  "https://images.unsplash.com/photo-1523987355523-c7b5b0723c34?auto=format&fit=crop&w=400&q=80", // Helado
  "https://images.unsplash.com/photo-1464306076886-debca5e8a6b0?auto=format&fit=crop&w=400&q=80", // Paleta
  "https://images.unsplash.com/photo-1519864341194-1a5b53c1b3b7?auto=format&fit=crop&w=400&q=80", // Galletas/dulces
  "https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=400&q=80", // Maracumango/vaso colorido
];

const instagramUrl = "https://www.instagram.com/tu_usuario/"; // Cambia por tu IG real

const GallerySection = () => (
  <section className="py-16 px-4 sm:px-6 lg:px-8 bg-gradient-to-br from-orange-50 to-yellow-50 text-center">
    <h2 className="text-4xl sm:text-5xl font-extrabold text-gray-900 mb-8">
      {/* Cambia aquí el nombre */}
      Momentos Olè Mita
    </h2>
    <div className="grid grid-cols-2 sm:grid-cols-3 gap-4 max-w-4xl mx-auto">
      {images.map((img, idx) => (
        <a key={idx} href={instagramUrl} target="_blank" rel="noopener noreferrer">
          <img
            src={img}
            alt={`ole mita galería ${idx + 1}`}
            className="rounded-2xl shadow-lg hover:scale-105 hover:shadow-2xl transition-transform duration-300 cursor-pointer object-cover w-full h-52"
          />
        </a>
      ))}
    </div>
    <a
      href={instagramUrl}
      target="_blank"
      rel="noopener noreferrer"
      className="inline-block mt-8 text-orange-600 font-bold text-xl hover:underline"
    >
      Ver más en Instagram →
    </a>
  </section>
);

export default GallerySection;
