import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

// Personaliza las flechas
function NextArrow(props) {
  const { onClick } = props;
  return (
    <button
      className="absolute right-3 top-1/2 -translate-y-1/2 z-10 bg-white hover:bg-orange-500 text-orange-500 hover:text-white rounded-full shadow-xl p-3 text-3xl transition-all border border-orange-100"
      onClick={onClick}
      aria-label="Siguiente"
      style={{ outline: 'none', border: 'none' }}
    >›</button>
  );
}

function PrevArrow(props) {
  const { onClick } = props;
  return (
    <button
      className="absolute left-3 top-1/2 -translate-y-1/2 z-10 bg-white hover:bg-orange-500 text-orange-500 hover:text-white rounded-full shadow-xl p-3 text-3xl transition-all border border-orange-100"
      onClick={onClick}
      aria-label="Anterior"
      style={{ outline: 'none', border: 'none' }}
    >‹</button>
  );
}

const ShopProductGrid = ({ products, onViewDetails }) => {
  const settings = {
    dots: true,
    infinite: products.length > 5,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3500,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    responsive: [
      {
        breakpoint: 1200,
        settings: { slidesToShow: 4 }
      },
      {
        breakpoint: 900,
        settings: { slidesToShow: 3 }
      },
      {
        breakpoint: 640,
        settings: { slidesToShow: 1 }
      }
    ]
  };

  return (
    <section className="py-12 px-4 sm:px-6 lg:px-8 bg-gradient-to-br from-white to-gray-50 relative">
      <h2 className="text-4xl sm:text-5xl font-extrabold text-center text-gray-900 mb-10 sm:mb-12">
        Nuestros Productos Estrella
      </h2>
      <div className="relative max-w-7xl mx-auto">
        <Slider {...settings}>
          {products.map((product) => (
            <div key={product.id} className="px-2">
              <div className="bg-white rounded-2xl shadow-xl overflow-hidden transform transition-all duration-300 ease-in-out hover:scale-105 hover:shadow-orange-300 hover:shadow-2xl group"
>
                <div className="relative overflow-hidden">
                  <img
                    src={product.image}
                    alt={product.name}
                    className="w-full h-48 sm:h-56 md:h-64 object-cover transition-transform duration-300 group-hover:scale-110"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-end p-4 sm:p-6">
                    <button
                      onClick={() => onViewDetails && onViewDetails(product)}
                      className="w-full py-2 sm:py-3 bg-orange-600 text-white font-semibold rounded-lg opacity-0 group-hover:opacity-100 transform translate-y-4 group-hover:translate-y-0 transition-all duration-300 ease-in-out text-base sm:text-lg"
                    >
                      Ver Detalles
                    </button>
                  </div>
                </div>
                <div className="p-4 sm:p-6">
                  <h3 className="text-xl sm:text-2xl font-semibold text-gray-800 mb-1 sm:mb-2">{product.name}</h3>
                  <p className="text-lg sm:text-xl text-orange-600 font-bold">${product.price} MXN</p>
                </div>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </section>
  );
};

export default ShopProductGrid;
