import React, { useState } from 'react';
import { doc, updateDoc } from "firebase/firestore";
import { db } from "../firebase"; // o "./firebase" según tu estructura

const AdminPanel = ({
  onLogout,
  bannerInfo,
  onUpdateBanner,
  products,
  onAddProduct,
  onUpdateProduct,
  onDeleteProduct,
  aboutInfo,
  onUpdateAbout,
  contactInfo,
  onUpdateContact,
  orders,
  onUpdateOrderStatus,
  categories,
  onAddCategory,
  onUpdatePassword,
}) => {
  const [activeTab, setActiveTab] = useState('banner');
  const [newBannerTitle, setNewBannerTitle] = useState(bannerInfo.title);
  const [newBannerSubtitle, setNewBannerSubtitle] = useState(bannerInfo.subtitle);
  const [newBannerVideo, setNewBannerVideo] = useState(bannerInfo.video || '');
  const [newBannerImages, setNewBannerImages] = useState(bannerInfo.images || []);
  const [newProductName, setNewProductName] = useState('');
  const [newProductPrice, setNewProductPrice] = useState('');
  const [newProductImage, setNewProductImage] = useState('');
  const [newProductCategory, setNewProductCategory] = useState('');
  const [editProductId, setEditProductId] = useState(null);
  const [editProductName, setEditProductName] = useState('');
  const [editProductPrice, setEditProductPrice] = useState('');
  const [editProductImage, setEditProductImage] = useState('');
  const [editProductCategory, setEditProductCategory] = useState('');
  const [newAboutTitle, setNewAboutTitle] = useState(aboutInfo.title);
  const [newAboutParagraph1, setNewAboutParagraph1] = useState(aboutInfo.paragraph1);
  const [newAboutParagraph2, setNewAboutParagraph2] = useState(aboutInfo.paragraph2);
  const [newAboutImage, setNewAboutImage] = useState(aboutInfo.image);
  const [newContactEmail, setNewContactEmail] = useState(contactInfo.email);
  const [newContactPhone, setNewContactPhone] = useState(contactInfo.phone);
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [newCategoryName, setNewCategoryName] = useState('');
  const [selectedOrder, setSelectedOrder] = useState(null);

const handleUpdateBanner = async () => {
  try {
    const docRef = doc(db, "config", "mainBanner");
    await updateDoc(docRef, {
      title: newBannerTitle,
      subtitle: newBannerSubtitle,
videos: [newBannerVideo, ...newBannerImages.filter(url => url.endsWith('.mp4'))],
images: newBannerImages.filter(url => !url.endsWith('.mp4')),
    });
    alert('Banner actualizado!');
  } catch (error) {
    alert('Error al actualizar el banner');
    console.error(error);
  }
};

  const handleAddProduct = () => {
    if (newProductName && newProductPrice && newProductImage && newProductCategory) {
      onAddProduct({
        id: products.length + 1, // Simple ID generation
        name: newProductName,
        price: newProductPrice,
        image: newProductImage,
        category: newProductCategory,
      });
      setNewProductName('');
      setNewProductPrice('');
      setNewProductImage('');
      setNewProductCategory('');
      alert('Producto agregado!');
    } else {
      alert('Por favor, rellena todos los campos del producto.');
    }
  };

  const startEditProduct = (product) => {
    setEditProductId(product.id);
    setEditProductName(product.name);
    setEditProductPrice(product.price);
    setEditProductImage(product.image);
    setEditProductCategory(product.category);
  };

  const handleUpdateProduct = () => {
    if (editProductId && editProductName && editProductPrice && editProductImage && editProductCategory) {
      onUpdateProduct({
        id: editProductId,
        name: editProductName,
        price: editProductPrice,
        image: editProductImage,
        category: editProductCategory,
      });
      setEditProductId(null);
      alert('Producto actualizado!');
    } else {
      alert('Por favor, rellena todos los campos del producto a editar.');
    }
  };

  const handleDeleteProduct = (id) => {
    if (window.confirm('¿Estás seguro de que quieres eliminar este producto?')) {
      onDeleteProduct(id);
      alert('Producto eliminado!');
    }
  };

  const handleUpdateAbout = () => {
    onUpdateAbout({
      title: newAboutTitle,
      paragraph1: newAboutParagraph1,
      paragraph2: newAboutParagraph2,
      image: newAboutImage,
    });
    alert('Información "Nosotros" actualizada!');
  };

  const handleUpdateContact = () => {
    onUpdateContact({
      email: newContactEmail,
      phone: newContactPhone,
    });
    alert('Información de contacto actualizada!');
  };

  const handlePasswordChange = () => {
    if (newPassword && newPassword === confirmNewPassword) {
      onUpdatePassword(newPassword);
      setNewPassword('');
      setConfirmNewPassword('');
      alert('Contraseña cambiada con éxito!');
    } else {
      alert('Las contraseñas no coinciden o están vacías.');
    }
  };

  const handleAddCategory = () => {
    if (newCategoryName && !categories.includes(newCategoryName)) {
      onAddCategory(newCategoryName);
      setNewCategoryName('');
      alert('Categoría agregada!');
    } else {
      alert('La categoría ya existe o está vacía.');
    }
  };

  const handleViewOrderDetails = (order) => {
    setSelectedOrder(order);
  };

  const handleCloseOrderDetails = () => {
    setSelectedOrder(null);
  };

  const handleOrderStatusChange = (orderId, newStatus) => {
    onUpdateOrderStatus(orderId, newStatus);
    if (selectedOrder && selectedOrder.id === orderId) {
      setSelectedOrder((prevOrder) => ({ ...prevOrder, status: newStatus }));
    }
  };

  return (
    <div className="fixed inset-0 bg-gray-100 flex flex-col z-50">
      <header className="bg-gray-800 text-white p-4 flex justify-between items-center shadow-md">
        <h1 className="text-2xl font-bold">Panel Administrativo Olè Mita</h1>
        <button
          onClick={onLogout}
          className="px-4 py-2 bg-red-600 rounded-md hover:bg-red-700 transition-colors"
        >
          Cerrar Sesión
        </button>
      </header>

      <div className="flex flex-1 overflow-hidden">
        <nav className="w-64 bg-gray-900 text-white p-4 flex flex-col space-y-2 shadow-lg">
          <button
            onClick={() => setActiveTab('banner')}
            className={`py-2 px-4 rounded-md text-left ${activeTab === 'banner' ? 'bg-orange-600' : 'hover:bg-gray-700'}`}
          >
            Banner Principal
          </button>
          <button
            onClick={() => setActiveTab('products')}
            className={`py-2 px-4 rounded-md text-left ${activeTab === 'products' ? 'bg-orange-600' : 'hover:bg-gray-700'}`}
          >
            Productos
          </button>
          <button
            onClick={() => setActiveTab('about')}
            className={`py-2 px-4 rounded-md text-left ${activeTab === 'about' ? 'bg-orange-600' : 'hover:bg-gray-700'}`}
          >
            Sección Nosotros
          </button>
          <button
            onClick={() => setActiveTab('contact')}
            className={`py-2 px-4 rounded-md text-left ${activeTab === 'contact' ? 'bg-orange-600' : 'hover:bg-gray-700'}`}
          >
            Información de Contacto
          </button>
          <button
            onClick={() => setActiveTab('orders')}
            className={`py-2 px-4 rounded-md text-left ${activeTab === 'orders' ? 'bg-orange-600' : 'hover:bg-gray-700'}`}
          >
            Pedidos
          </button>
          <button
            onClick={() => setActiveTab('categories')}
            className={`py-2 px-4 rounded-md text-left ${activeTab === 'categories' ? 'bg-orange-600' : 'hover:bg-gray-700'}`}
          >
            Categorías
          </button>
          <button
            onClick={() => setActiveTab('password')}
            className={`py-2 px-4 rounded-md text-left ${activeTab === 'password' ? 'bg-orange-600' : 'hover:bg-gray-700'}`}
          >
            Cambiar Contraseña
          </button>
        </nav>

        <div className="flex-1 p-6 overflow-y-auto">
          {activeTab === 'banner' && (
            <div className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-2xl font-bold mb-4">Modificar Banner Principal</h2>
              <div className="space-y-4">
                <div>
                  <label className="block text-gray-700">Título del Banner</label>
                  <input
                    type="text"
                    className="w-full p-2 border rounded-md"
                    value={newBannerTitle}
                    onChange={(e) => setNewBannerTitle(e.target.value)}
                  />
                </div>
                <div>
                  <label className="block text-gray-700">Subtítulo del Banner</label>
                  <textarea
                    className="w-full p-2 border rounded-md"
                    value={newBannerSubtitle}
                    onChange={(e) => setNewBannerSubtitle(e.target.value)}
                  ></textarea>
                </div>
                <div>
                  <label className="block text-gray-700">URL del Video (opcional)</label>
                  <input
                    type="text"
                    className="w-full p-2 border rounded-md"
                    value={newBannerVideo}
                    onChange={(e) => setNewBannerVideo(e.target.value)}
                    placeholder="Ej: https://www.ejemplo.com/video.mp4"
                  />
                  {newBannerVideo && <video src={newBannerVideo} controls className="mt-2 h-32 object-cover" />}
                </div>
                <div>
                  <label className="block text-gray-700">URLs de Imágenes (separadas por coma)</label>
                  <textarea
                    className="w-full p-2 border rounded-md"
                    value={newBannerImages.join(', ')}
                    onChange={(e) => setNewBannerImages(e.target.value.split(',').map(url => url.trim()))}
                    placeholder="Ej: url1.jpg, url2.jpg, url3.jpg"
                  ></textarea>
                  <div className="flex flex-wrap gap-2 mt-2">
                    {newBannerImages.map((img, index) => img && <img key={index} src={img} alt={`Preview ${index}`} className="h-20 w-20 object-cover rounded-md" />)}
                  </div>
                </div>
                <button
                  onClick={handleUpdateBanner}
                  className="px-4 py-2 bg-orange-600 text-white rounded-md hover:bg-orange-700"
                >
                  Actualizar Banner
                </button>
              </div>
            </div>
          )}

          {activeTab === 'products' && (
            <div className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-2xl font-bold mb-4">Gestionar Productos</h2>
              <div className="mb-6">
                <h3 className="text-xl font-semibold mb-2">Agregar Nuevo Producto</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-gray-700">Nombre</label>
                    <input
                      type="text"
                      className="w-full p-2 border rounded-md"
                      value={newProductName}
                      onChange={(e) => setNewProductName(e.target.value)}
                    />
                  </div>
                  <div>
                    <label className="block text-gray-700">Precio</label>
                    <input
                      type="text"
                      className="w-full p-2 border rounded-md"
                      value={newProductPrice}
                      onChange={(e) => setNewProductPrice(e.target.value)}
                    />
                  </div>
                  <div>
                    <label className="block text-gray-700">URL Imagen</label>
                    <input
                      type="text"
                      className="w-full p-2 border rounded-md"
                      value={newProductImage}
                      onChange={(e) => setNewProductImage(e.target.value)}
                    />
                  </div>
                  <div>
                    <label className="block text-gray-700">Categoría</label>
                    <select
                      className="w-full p-2 border rounded-md"
                      value={newProductCategory}
                      onChange={(e) => setNewProductCategory(e.target.value)}
                    >
                      <option value="">Selecciona una categoría</option>
                      {categories.map((cat) => (
                        <option key={cat} value={cat}>{cat}</option>
                      ))}
                    </select>
                  </div>
                </div>
                <button
                  onClick={handleAddProduct}
                  className="mt-4 px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700"
                >
                  Agregar Producto
                </button>
              </div>

              <h3 className="text-xl font-semibold mb-2">Productos Existentes</h3>
              <div className="overflow-x-auto">
                <table className="min-w-full bg-white border border-gray-200">
                  <thead>
                    <tr>
                      <th className="py-2 px-4 border-b">ID</th>
                      <th className="py-2 px-4 border-b">Nombre</th>
                      <th className="py-2 px-4 border-b">Precio</th>
                      <th className="py-2 px-4 border-b">Categoría</th>
                      <th className="py-2 px-4 border-b">Acciones</th>
                    </tr>
                  </thead>
                  <tbody>
                    {products.map((product) => (
                      <tr key={product.id}>
                        <td className="py-2 px-4 border-b">{product.id}</td>
                        <td className="py-2 px-4 border-b">
                          {editProductId === product.id ? (
                            <input
                              type="text"
                              value={editProductName}
                              onChange={(e) => setEditProductName(e.target.value)}
                              className="w-full p-1 border rounded-md"
                            />
                          ) : (
                            product.name
                          )}
                        </td>
                        <td className="py-2 px-4 border-b">
                          {editProductId === product.id ? (
                            <input
                              type="text"
                              value={editProductPrice}
                              onChange={(e) => setEditProductPrice(e.target.value)}
                              className="w-full p-1 border rounded-md"
                            />
                          ) : (
                            product.price
                          )}
                        </td>
                        <td className="py-2 px-4 border-b">
                          {editProductId === product.id ? (
                            <select
                              value={editProductCategory}
                              onChange={(e) => setEditProductCategory(e.target.value)}
                              className="w-full p-1 border rounded-md"
                            >
                              {categories.map((cat) => (
                                <option key={cat} value={cat}>{cat}</option>
                              ))}
                            </select>
                          ) : (
                            product.category
                          )}
                        </td>
                        <td className="py-2 px-4 border-b">
                          {editProductId === product.id ? (
                            <button
                              onClick={handleUpdateProduct}
                              className="px-3 py-1 bg-blue-600 text-white rounded-md hover:bg-blue-700 mr-2"
                            >
                              Guardar
                            </button>
                          ) : (
                            <button
                              onClick={() => startEditProduct(product)}
                              className="px-3 py-1 bg-yellow-600 text-white rounded-md hover:bg-yellow-700 mr-2"
                            >
                              Editar
                            </button>
                          )}
                          <button
                            onClick={() => handleDeleteProduct(product.id)}
                            className="px-3 py-1 bg-red-600 text-white rounded-md hover:bg-red-700"
                          >
                            Eliminar
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {activeTab === 'about' && (
            <div className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-2xl font-bold mb-4">Modificar Sección "Nosotros"</h2>
              <div className="space-y-4">
                <div>
                  <label className="block text-gray-700">Título</label>
                  <input
                    type="text"
                    className="w-full p-2 border rounded-md"
                    value={newAboutTitle}
                    onChange={(e) => setNewAboutTitle(e.target.value)}
                  />
                </div>
                <div>
                  <label className="block text-gray-700">Párrafo 1</label>
                  <textarea
                    className="w-full p-2 border rounded-md"
                    value={newAboutParagraph1}
                    onChange={(e) => setNewAboutParagraph1(e.target.value)}
                  ></textarea>
                </div>
                <div>
                  <label className="block text-gray-700">Párrafo 2</label>
                  <textarea
                    className="w-full p-2 border rounded-md"
                    value={newAboutParagraph2}
                    onChange={(e) => setNewAboutParagraph2(e.target.value)}
                  ></textarea>
                </div>
                <div>
                  <label className="block text-gray-700">URL de la Imagen</label>
                  <input
                    type="text"
                    className="w-full p-2 border rounded-md"
                    value={newAboutImage}
                    onChange={(e) => setNewAboutImage(e.target.value)}
                  />
                  {newAboutImage && <img src={newAboutImage} alt="Preview" className="mt-2 h-32 object-cover" />}
                </div>
                <button
                  onClick={handleUpdateAbout}
                  className="px-4 py-2 bg-orange-600 text-white rounded-md hover:bg-orange-700"
                >
                  Actualizar "Nosotros"
                </button>
              </div>
            </div>
          )}

          {activeTab === 'contact' && (
            <div className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-2xl font-bold mb-4">Modificar Información de Contacto</h2>
              <div className="space-y-4">
                <div>
                  <label className="block text-gray-700">Email</label>
                  <input
                    type="email"
                    className="w-full p-2 border rounded-md"
                    value={newContactEmail}
                    onChange={(e) => setNewContactEmail(e.target.value)}
                  />
                </div>
                <div>
                  <label className="block text-gray-700">Teléfono</label>
                  <input
                    type="text"
                    className="w-full p-2 border rounded-md"
                    value={newContactPhone}
                    onChange={(e) => setNewContactPhone(e.target.value)}
                  />
                </div>
                <button
                  onClick={handleUpdateContact}
                  className="px-4 py-2 bg-orange-600 text-white rounded-md hover:bg-orange-700"
                >
                  Actualizar Contacto
                </button>
              </div>
            </div>
          )}

          {activeTab === 'orders' && (
            <div className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-2xl font-bold mb-4">Pedidos Realizados</h2>
              {orders.length > 0 ? (
                <div className="overflow-x-auto">
                  <table className="min-w-full bg-white border border-gray-200">
                    <thead>
                      <tr>
                        <th className="py-2 px-4 border-b">ID Pedido</th>
                        <th className="py-2 px-4 border-b">Cliente</th>
                        <th className="py-2 px-4 border-b">Total</th>
                        <th className="py-2 px-4 border-b">Estado</th>
                        <th className="py-2 px-4 border-b">Acciones</th>
                      </tr>
                    </thead>
                    <tbody>
                      {orders.map((order) => (
                        <tr key={order.id}>
                          <td className="py-2 px-4 border-b">{order.id}</td>
                          <td className="py-2 px-4 border-b">{order.customerName}</td>
                          <td className="py-2 px-4 border-b">${order.total}</td>
                          <td className="py-2 px-4 border-b">
                            <select
                              value={order.status}
                              onChange={(e) => handleOrderStatusChange(order.id, e.target.value)}
                              className="p-1 border rounded-md"
                            >
                              <option value="Pendiente">Pendiente</option>
                              <option value="Procesando">Procesando</option>
                              <option value="Enviado">Enviado</option>
                              <option value="Entregado">Entregado</option>
                              <option value="Cancelado">Cancelado</option>
                            </select>
                          </td>
                          <td className="py-2 px-4 border-b">
                            <button
                              onClick={() => handleViewOrderDetails(order)}
                              className="px-3 py-1 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                            >
                              Ver Detalles
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ) : (
                <p className="text-gray-600">No hay pedidos realizados aún.</p>
              )}

              {selectedOrder && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
                  <div className="bg-white rounded-lg shadow-xl p-8 w-full max-w-lg relative">
                    <button
                      onClick={handleCloseOrderDetails}
                      className="absolute top-4 right-4 text-gray-500 hover:text-gray-700 text-2xl"
                    >
                      &times;
                    </button>
                    <h3 className="text-2xl font-bold mb-4">Detalles del Pedido #{selectedOrder.id}</h3>
                    <div className="space-y-2">
                      <p><strong>Cliente:</strong> {selectedOrder.customerName}</p>
                      <p><strong>Email:</strong> {selectedOrder.customerEmail}</p>
                      <p><strong>Teléfono:</strong> {selectedOrder.customerPhone}</p>
                      <p><strong>Total:</strong> ${selectedOrder.total}</p>
                      <p><strong>Estado:</strong> {selectedOrder.status}</p>
                      <p><strong>Productos:</strong></p>
                      <ul className="list-disc list-inside ml-4">
                        {selectedOrder.products.map((p, idx) => (
                          <div key={idx}>{p.name} (x{p.quantity})</div>
                        ))}
                      </ul>
                    </div>
                  </div>
                </div>
              )}
            </div>
          )}

          {activeTab === 'categories' && (
            <div className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-2xl font-bold mb-4">Gestionar Categorías</h2>
              <div className="mb-6">
                <h3 className="text-xl font-semibold mb-2">Agregar Nueva Categoría</h3>
                <div className="flex space-x-2">
                  <input
                    type="text"
                    className="flex-1 p-2 border rounded-md"
                    value={newCategoryName}
                    onChange={(e) => setNewCategoryName(e.target.value)}
                    placeholder="Nombre de la categoría"
                  />
                  <button
                    onClick={handleAddCategory}
                    className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700"
                  >
                    Agregar
                  </button>
                </div>
              </div>
              <div>
                <h3 className="text-xl font-semibold mb-2">Categorías Existentes</h3>
                <ul className="list-disc list-inside">
                  {categories.map((cat) => (
                    <li key={cat} className="py-1">{cat}</li>
                  ))}
                </ul>
              </div>
            </div>
          )}

          {activeTab === 'password' && (
            <div className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-2xl font-bold mb-4">Cambiar Contraseña</h2>
              <div className="space-y-4">
                <div>
                  <label className="block text-gray-700">Nueva Contraseña</label>
                  <input
                    type="password"
                    className="w-full p-2 border rounded-md"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                  />
                </div>
                <div>
                  <label className="block text-gray-700">Confirmar Nueva Contraseña</label>
                  <input
                    type="password"
                    className="w-full p-2 border rounded-md"
                    value={confirmNewPassword}
                    onChange={(e) => setConfirmNewPassword(e.target.value)}
                  />
                </div>
                <button
                  onClick={handlePasswordChange}
                  className="px-4 py-2 bg-orange-600 text-white rounded-md hover:bg-orange-700"
                >
                  Cambiar Contraseña
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
