import React, { useState, useEffect, useRef } from 'react';

const ShopProductGrid = ({ products, visibleCount = 3, autoSlideInterval = 4000, onViewDetails }) => {
  const [startIndex, setStartIndex] = useState(0);
  const timeoutRef = useRef(null);

  // Cambia el slide automáticamente cada X milisegundos
  useEffect(() => {
    timeoutRef.current = setTimeout(() => {
      handleNext();
    }, autoSlideInterval);
    return () => clearTimeout(timeoutRef.current);
    // eslint-disable-next-line
  }, [startIndex, products, autoSlideInterval]);

  const handlePrev = () => {
    setStartIndex(prev =>
      prev === 0 ? Math.max(products.length - visibleCount, 0) : prev - 1
    );
  };

  const handleNext = () => {
    setStartIndex(prev =>
      prev + visibleCount >= products.length ? 0 : prev + 1
    );
  };

  // Selecciona los productos visibles en el slider
  const visibleProducts = products.slice(startIndex, startIndex + visibleCount);

  return (
    <section className="py-12 px-4 sm:px-6 lg:px-8 bg-gradient-to-br from-white to-gray-50 relative">
      <h2 className="text-4xl sm:text-5xl font-extrabold text-center text-gray-900 mb-10 sm:mb-12">
        Nuestros Productos Estrella
      </h2>
      <div className="flex items-center justify-center relative">
        {/* Flecha izquierda */}
        <button
          className="absolute left-0 z-10 bg-white rounded-full shadow-lg px-3 py-1 text-2xl font-bold hover:bg-orange-100 transition"
          onClick={handlePrev}
          aria-label="Anterior"
          style={{ top: '50%', transform: 'translateY(-50%)' }}
        >
          &#8592;
        </button>

        <div className="flex gap-6 sm:gap-8 lg:gap-10">
          {visibleProducts.length > 0 ? (
            visibleProducts.map((product) => (
              <div
                key={product.id}
                className="bg-white rounded-2xl shadow-xl overflow-hidden transform transition-all duration-300 ease-in-out hover:scale-105 hover:shadow-2xl group w-72"
              >
                <div className="relative overflow-hidden">
                  <img
                    src={product.image}
                    alt={product.name}
                    className="w-full h-48 sm:h-56 md:h-64 object-cover transition-transform duration-300 group-hover:scale-110"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-end p-4 sm:p-6">
                    <button
                      onClick={() => onViewDetails && onViewDetails(product)}
                      className="w-full py-2 sm:py-3 bg-orange-600 text-white font-semibold rounded-lg opacity-0 group-hover:opacity-100 transform translate-y-4 group-hover:translate-y-0 transition-all duration-300 ease-in-out text-base sm:text-lg"
                    >
                      Ver Detalles
                    </button>
                  </div>
                </div>
                <div className="p-4 sm:p-6">
                  <h3 className="text-xl sm:text-2xl font-semibold text-gray-800 mb-1 sm:mb-2">{product.name}</h3>
                  <p className="text-lg sm:text-xl text-orange-600 font-bold">${product.price} MXN</p>
                </div>
              </div>
            ))
          ) : (
            <p className="text-center text-gray-600 text-lg sm:text-xl col-span-full">No se encontraron productos que coincidan con tu búsqueda.</p>
          )}
        </div>

        {/* Flecha derecha */}
        <button
          className="absolute right-0 z-10 bg-white rounded-full shadow-lg px-3 py-1 text-2xl font-bold hover:bg-orange-100 transition"
          onClick={handleNext}
          aria-label="Siguiente"
          style={{ top: '50%', transform: 'translateY(-50%)' }}
        >
          &#8594;
        </button>
      </div>
    </section>
  );
};

export default ShopProductGrid;
