import React, { useState, useEffect, useRef } from 'react';

const ShopHeroSection = ({ bannerVideos }) => {
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);
  const videoRef = useRef(null);

  useEffect(() => {
    if (bannerVideos && bannerVideos.length > 0) {
      const videoElement = videoRef.current;

      const handleVideoEnd = () => {
        setCurrentVideoIndex((prevIndex) => (prevIndex + 1) % bannerVideos.length);
      };

if (videoElement) {
  videoElement.addEventListener('ended', handleVideoEnd);
  videoElement.load(); // Cargar el nuevo video

  // Espera a que esté listo antes de intentar play
  const playPromise = videoElement.play();
  if (playPromise !== undefined) {
    playPromise.catch(() => {
      // Ignora el error, normalmente por autoplay bloqueado o cambio de video rápido
    });
  }
}

      return () => {
        if (videoElement) {
          videoElement.removeEventListener('ended', handleVideoEnd);
        }
      };
    }
  }, [currentVideoIndex, bannerVideos]);

  return (
    <section className="relative w-full h-[50vh] md:h-[70vh] lg:h-[80vh] overflow-hidden">
      {/* Contenedor del video */}
      {bannerVideos && bannerVideos.length > 0 && (
        <video
          ref={videoRef}
          className="absolute inset-0 w-full h-full object-cover opacity-70 transition-opacity duration-1000"
          src={bannerVideos[currentVideoIndex]}
          autoPlay
          muted
          playsInline
        />
      )}
      {/* Eliminar los fondos dinámicos de colores */}
      {/* <div className="absolute inset-0 z-0">
        <div className="absolute top-1/4 left-1/4 w-48 h-48 sm:w-64 sm:h-64 bg-orange-200 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob"></div>
        <div className="absolute top-1/2 right-1/4 w-56 h-56 sm:w-72 sm:h-72 bg-red-200 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-2000"></div>
        <div className="absolute bottom-1/4 left-1/3 w-64 h-64 sm:w-80 sm:h-80 bg-yellow-200 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-4000"></div>
      </div> */}
    </section>
  );
};

export default ShopHeroSection;
