import React, { useState, useEffect, useRef } from 'react';
import BannerSkeleton from './BannerSkeleton'; // Importa el Skeleton

const ShopHeroSection = ({ bannerVideos, isLoading }) => {
  // TODOS los hooks van aquí DENTRO de la función
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);
  const [isFading, setIsFading] = useState(false);
  const videoRef = useRef(null);

  useEffect(() => {
    if (!bannerVideos || bannerVideos.length === 0) return;

    const videoElement = videoRef.current;

    const handleVideoEnd = () => {
      setIsFading(true); // Empieza fade out
      setTimeout(() => {
        setCurrentVideoIndex((prevIndex) => (prevIndex + 1) % bannerVideos.length);
        setIsFading(false); // Hace fade in al nuevo video
      }, 600); // 600 ms de transición (puedes ajustar)
    };

    if (videoElement) {
      videoElement.addEventListener('ended', handleVideoEnd);
      // Fade in al cargar el video
      videoElement.style.opacity = 0;
      setTimeout(() => {
        videoElement.style.opacity = 0.7; // Ajusta si usas otro valor
      }, 60);
    }

    return () => {
      if (videoElement) {
        videoElement.removeEventListener('ended', handleVideoEnd);
      }
    };
  }, [currentVideoIndex, bannerVideos]);

  return (
    <section className="relative w-full h-[50vh] md:h-[70vh] lg:h-[80vh] overflow-hidden">
      {isLoading ? (
        <BannerSkeleton />
      ) : (
        bannerVideos && bannerVideos.length > 0 && (
          <video
            ref={videoRef}
            className={`absolute inset-0 w-full h-full object-cover transition-opacity duration-700 ${isFading ? 'opacity-0' : 'opacity-70'}`}
            src={bannerVideos[currentVideoIndex]}
            autoPlay
            muted
            playsInline
          />
        )
      )}
    </section>
  );
};

export default ShopHeroSection;
